javascript:
frappe.query_reports["Item Status Report"] = {
    "filters": [
        {
            "fieldname": "name",
            "label": "Item",
            "fieldtype": "Link",
            "options": "Item"
        },
         {
            "fieldname": "voucher_no",
            "label": "Voucher",
            "fieldtype": "Data",

        },
          {
			fieldname: "from_date",
			label: __("From Date"),
			fieldtype: "Date",
		},
		{
			fieldname: "to_date",
			label: __("To Date"),
			fieldtype: "Date",
		},

    ],
    "formatter": function(value, row, column, data, default_formatter) {
        value = default_formatter(value, row, column, data);
        console.log(column.fieldname)
        if (column.fieldname == "in_qty" && data.in_qty == 1)
        {
        value = "<span style='color:Green'>" + value + "</span>";
        }
        if (column.fieldname == "out_qty" && data.out_qty == -1)
        {
        value = "<span style='color:red'>" + value + "</span>";
        }

        return value;
    },

};
